INSERT INTO `faqs` (`id`, `title`, `details`, `status`) VALUES
(1, 'I am having difficulties accessing the website. What\'s wrong?', '<p class=\"MsoListParagraphCxSpFirst\">First step is, please check if it only\r\nhappens when you are trying to load halalmarket.sg. Check and see if loading\r\nanother website works.&nbsp; If you still get\r\nthe same error message, you’ll need to contact your internet service provider.</p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\">For the best HalalMarket.SG experience,\r\nplease ensure that you are using one of the following supported browsers:</p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\">Google Chrome<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\">Mozilla Firefox<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\">Safari<o:p></o:p></p>', 1),
(3, 'Can I change my delivery time slot?', '<span style=\"color: rgb(70, 85, 65); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 16px;\"=\"\">Yes you can. Do contact the vendor directly using the hotline that they have provided in their shop.</span><br>', 1),
(4, 'When can I get my shopping delivered?', '<span style=\"color: rgb(70, 85, 65); font-family: \" open=\"\" sans\",=\"\" sans-serif;=\"\" font-size:=\"\" 16px;\"=\"\">Your shopping will be delivered based on the selected timings that you have chosen and also as determined by the vendor that have sold you the item or service.</span><br>', 0),
(5, 'How do I find products?', '<p class=\"MsoListParagraphCxSpFirst\">There are two ways for you to do that.</p><p class=\"MsoListParagraphCxSpFirst\">1. Product Search – Simply type the product\r\nyou are looking for into the search bar at the top of the site and click on the\r\nmagnifying glass icon or just press ENTER.</p><p class=\"MsoListParagraphCxSpMiddle\"><o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\">2. By Category – You can find all the main\r\nproduct categories in the Home Page and you can narrow your search further by\r\nselecting any of these categories to look for your desired product.</p><p class=\"MsoListParagraphCxSpLast\"><o:p></o:p></p>', 0),
(6, 'How do I buy?', '<div style=\"text-align: center;\"><div style=\"text-align: center;\"><p class=\"MsoListParagraphCxSpFirst\" style=\"text-align: left; text-indent: -18pt;\"><!--[if !supportLists]-->1.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>Select all the products that you want, choose\r\nthe quantity and click ‘Add to cart’. You may also adjust the quantity or\r\nremove items once they are in your cart.<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: left; text-indent: -18pt;\"><!--[if !supportLists]-->2.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;</span>Once you have checked that the items and amount\r\nare correct, click Checkout.<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: left; text-indent: -18pt;\"><!--[if !supportLists]-->3.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>Fill up your Billing Address details.<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpMiddle\" style=\"text-align: left; text-indent: -18pt;\"><!--[if !supportLists]-->4.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>Choose your Payment method.<o:p></o:p></p>\r\n\r\n<p class=\"MsoListParagraphCxSpLast\" style=\"text-align: left; text-indent: -18pt;\"><!--[if !supportLists]-->5.<span style=\"font-variant-numeric: normal; font-variant-east-asian: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: &quot;Times New Roman&quot;;\">&nbsp;&nbsp;&nbsp;&nbsp;</span>You will receive an order confirmation shortly\r\nvia your registered email.<o:p></o:p></p></div></div>', 0);
